                    //            // current_display_engine = value;
                    //            // Display.PrepareDisplayVars(Display.CurrentDisplayMode);
                    //            // Display.DrawBackground();
                    //            pause_DisplayThread = false;
                    //        }
                    // Display.CurrentDisplayEngine = value;
