        public void computeMKIIPAVolts()
        {
            float adc = 0;
            float addadc = 0;
            float volt_div = (22.0f + 1.0f) / 1.1f; // Voltage divider (R1 + R2) / R2

            for (int count = 0; count < 100; count++)
            {
                adc = NetworkIO.getUserADC0();
                addadc += adc;
                Thread.Sleep(1);
            }
            adc = addadc / 100.0f;

            float volts = (adc / 4095.0f) * 5.0f;
            volts = volts * volt_div;
            MKIIPAVolts = volts;
        }

        public void computeMKIIPAAmps()
        {
            float adc = 0;
            float addadc = 0;
            float voff = 360.0f, sens = 120.0f;

            if (anan7000dpresent)
            {
                voff = 340.0f;
                sens = 88.0f;
            }
            for (int count = 0; count < 100; count++)
            {
                adc = NetworkIO.getUserADC1(); //JanusAudio.getAIN4();
                addadc += adc;
                Thread.Sleep(1);
            }
            adc = addadc / 100.0f;
            float fwdvolts = ((float)adc * 5000.0f) / 4095.0f;
            if (fwdvolts < 0) fwdvolts = 0.0f;
            float amps = ((fwdvolts - voff) / sens);
            //  float amps = (0.01f * adc - 2.91f);
            if (amps < 0) amps = 0.0f;
            MKIIPAAmps = amps;
        }

        public float computeRefPower()
        {
            float adc = 0;
            const float alpha = 0.90f;

            float bridge_volt = 0;
            float refvoltage = 0;
            int adc_cal_offset = 0;

            switch (current_hpsdr_model)
            {
                case HPSDRModel.ANAN100:
                case HPSDRModel.ANAN100B:
                    bridge_volt = 0.095f;
                    if (tx_band == Band.B6M)
                        bridge_volt = 0.5f;
                    refvoltage = 3.3f;
                    adc_cal_offset = 3;
                    break;
                case HPSDRModel.ANAN100D:
                    bridge_volt = 0.095f;
                    if (tx_band == Band.B6M)
                        bridge_volt = 0.5f;
                    refvoltage = 3.3f;
                    adc_cal_offset = 3;
                    break;
                case HPSDRModel.ANAN200D:
                    bridge_volt = 0.108f;
                    if (tx_band == Band.B6M)
                        bridge_volt = 0.5f;
                    refvoltage = 5.0f;
                    adc_cal_offset = 2;
                    break;
                case HPSDRModel.ANAN7000D:
                    bridge_volt = 0.15f;
                    if (tx_band == Band.B6M)
                        bridge_volt = 0.7f;
                    refvoltage = 5.0f;
                    adc_cal_offset = 28;
                    break;
                case HPSDRModel.ORIONMKII:
                case HPSDRModel.ANAN8000D:
                    bridge_volt = 0.08f;
                    if (tx_band == Band.B6M)
                        bridge_volt = 0.08f;
                    refvoltage = 5.0f;
                    adc_cal_offset = 16;
                    break;
                default:
                    bridge_volt = 0.09f;
                    if (tx_band == Band.B6M)
                        bridge_volt = 0.09f;
                    refvoltage = 3.3f;
                    adc_cal_offset = 3;
                    break;
            }

            //for (int count = 0; count < 50; count++)
            //{
            //    adc = JanusAudio.getRefPower();
            //    addadc += adc;
            //    Thread.Sleep(1);
            //}
            //adc = addadc / 50;
            adc = NetworkIO.getRevPower();

            if (adc < 0) adc = 0;

            float volts = (float)((adc - adc_cal_offset) / 4095.0 * refvoltage);
            if (volts < 0) volts = 0;
            float watts = (float)(Math.Pow(volts, 2) / bridge_volt);
            if (watts < 0) watts = 0;

            if (PAValues)
            {
                average_revadc = alpha * average_revadc + (1.0f - alpha) * adc;
                // SetupForm.textRevADCValue.Text = adc.ToString();
                SetupForm.textRevVoltage.Text = volts.ToString("f2") + " V";
            }

            return watts;
        }

