                                        case DSPMode.CWL:
                                            freq += (float)cw_pitch * 0.0000010;
                                            break;
                                        case DSPMode.CWU:
                                            freq -= (float)cw_pitch * 0.0000010;
                                            break;
                                        case DSPMode.DIGL:
                                            if (!ClickTuneFilter) freq += (float)digl_click_tune_offset * 0.0000010;
                                            break;
                                        case DSPMode.DIGU:
                                            if (!ClickTuneFilter) freq -= (float)digu_click_tune_offset * 0.0000010;
                                            break;
                                    }

                                    if (snap_to_click_tuning &&
