                                            VFOASubFreq = Math.Round(freq, 6);
                                        }
                                        else
                                        {
                                            if (e.Y <= picDisplay.Height / 2)
                                            {
                                                if (!rx1_spectrum_tune_drag)
                                                    VFOAFreq = Math.Round(freq, 6);
