                                    udXIT.Value = 0;
                                break;
                            default:
                                break;
                        }
                    }
                    else if (!near_notch &&
                              !agc_knee_drag &&
                              !agc_hang_drag &&
                              !gridminmaxadjust &&
                              !gridmaxadjust)// current_click_tune_mode == ClickTuneMode.Off) 
                    {
                        switch (Display.CurrentDisplayMode)
                        {
                            case DisplayMode.PANADAPTER:
