                        SetupForm.VACEnable = false;
                    }

                    if (new_mode != DSPMode.DIGU) SetDigiMode(1, "reset");
                    break;
                case DSPMode.DIGU:
                    radModeDIGU.BackColor = SystemColors.Control;
