                    Directory.CreateDirectory(AppDataPath + "\\DB_Archive\\");

                File.Copy(db_file_name, AppDataPath + "\\DB_Archive\\Thetis_" + file + "_" + datetime + ".xml");
                File.Delete(db_file_name);
            }
        }

