        // G8NJJ like CATBandGroup but covering SWL too
        // use the enum variables, because all 3 forms can be hidden when display collapsed
        public int BandGroup
        {
            get
            {
                if (whatisGEN)
                    return 2;
                else if (whatisVHF)
                    return 1;
                else
                    return 0;
            }
            set
            {
                if (value == 0)
                    btnBandHF_Click(btnBandHF, EventArgs.Empty);
