            switch (keyData)
            {
                case Keys.Down:
                case Keys.Up:
                case Keys.Left:
                case Keys.Right:
                    Console_KeyDown(this, new KeyEventArgs(keyData));
                    return true;
            }
            return base.ProcessDialogKey(keyData);
        }
