            string oldDigTXProfile = comboDigTXProfile.Text;
            string oldFMTXProfile = comboFMTXProfile.Text;
            string oldAMTXProfile = comboAMTXProfile.Text;

            BuildTXProfileCombos();

            switch (rx1_dsp_mode)
            {
                case DSPMode.DIGL:
                case DSPMode.DIGU:
                    comboDigTXProfile.Text = name;
                    comboTXProfile.Text = oldTXProfile;
                    comboFMTXProfile.Text = oldFMTXProfile;
                    comboAMTXProfile.Text = oldAMTXProfile;
                    break;
                case DSPMode.FM:
                    comboFMTXProfile.Text = name;
                    comboDigTXProfile.Text = oldDigTXProfile;
                    comboTXProfile.Text = oldTXProfile;
                    comboAMTXProfile.Text = oldAMTXProfile;
                    break;
                case DSPMode.AM:
                case DSPMode.SAM:
                    comboAMTXProfile.Text = name;
                    comboDigTXProfile.Text = oldDigTXProfile;
                    comboTXProfile.Text = oldTXProfile;
                    comboFMTXProfile.Text = oldFMTXProfile;
                    break;
                default:
                    comboTXProfile.Text = name;
                    comboDigTXProfile.Text = oldDigTXProfile;
                    comboFMTXProfile.Text = oldFMTXProfile;
                    comboAMTXProfile.Text = oldAMTXProfile;
                    break;
            }

            if (comboTXProfile.Text == "") comboTXProfile.Text = "Default";
            if (comboDigTXProfile.Text == "") comboDigTXProfile.Text = "Default";
            if (comboFMTXProfile.Text == "") comboFMTXProfile.Text = "Default";
            if (comboAMTXProfile.Text == "") comboAMTXProfile.Text = "Default";
        }

        // Diversity operation is on RX1; therefore, the 'rx1_rate' will be used as the diversity rate;
        public void UpdateDDCs(bool rx2_enabled)
        {
            if (initializing) return;
            int DDCEnable = 0;
            int DDC0 = 1, DDC1 = 2, DDC2 = 4, DDC3 = 8;
            int SyncEnable = 0;
            int[] Rate = new int[8];
            int i;
            int rx1_rate = SampleRateRX1;
            int rx2_rate = SampleRateRX2;
            int ps_rate = cmaster.PSrate;
            int P1_DDCConfig = 0;
            int P1_diversity = 0;
            int P1_rxcount = 0;
            int nddc = 0;
            int cntrl1 = 0;
            int cntrl2 = 0;
            if (diversity2)
                P1_diversity = 1;

            switch (current_hpsdr_model)
            {
                case HPSDRModel.ANAN100D:
                case HPSDRModel.ANAN200D:
                case HPSDRModel.ORIONMKII:
                case HPSDRModel.ANAN7000D:
                case HPSDRModel.ANAN8000D:
                    P1_rxcount = 5;                     // RX5 used for puresignal feedback
                    nddc = 5;
                    if (!mox)
                    {
                        if (diversity2)
                        {
                            P1_DDCConfig =
                            DDCEnable = DDC0;
                            SyncEnable = DDC1;
                            Rate[0] = rx1_rate;
                            Rate[1] = rx1_rate;
                            cntrl1 = rx_adc_ctrl1 & 0xff;
                            cntrl2 = rx_adc_ctrl2 & 0x3f;
                        }
                        else
                        {
                            P1_DDCConfig = 1;
                            DDCEnable = DDC2;
                            SyncEnable = 0;
                            //Rate[0] = rx1_rate;
                            //Rate[1] = rx2_rate; 
                            Rate[2] = rx1_rate;
                            cntrl1 = rx_adc_ctrl1 & 0xff;
                            cntrl2 = rx_adc_ctrl2 & 0x3f;
                        }
                    }
                    else
                    {
                        if (!diversity2 && !psform.PSEnabled)
                        {
                            P1_DDCConfig = 1;
                            DDCEnable = DDC2;
                            SyncEnable = 0;
                            Rate[2] = rx1_rate;
                            cntrl1 = rx_adc_ctrl1 & 0xff;
                            cntrl2 = rx_adc_ctrl2 & 0x3f;
                        }
                        else if (!diversity2 && psform.PSEnabled)
                        {
                            P1_DDCConfig = 3;
                            DDCEnable = DDC0 + DDC2;
                            SyncEnable = DDC1;
                            Rate[0] = ps_rate;
                            Rate[1] = ps_rate;
                            Rate[2] = rx1_rate;
                            cntrl1 = (rx_adc_ctrl1 & 0xf3) | 0x08;
                            cntrl2 = rx_adc_ctrl2 & 0x3f;
                        }
                        else if (diversity2 && psform.PSEnabled)
                        {
                            P1_DDCConfig = 3;
                            DDCEnable = DDC0 + DDC2;
                            SyncEnable = DDC1;
                            Rate[0] = ps_rate;
                            Rate[1] = ps_rate;
                            Rate[2] = rx1_rate;
                            cntrl1 = (rx_adc_ctrl1 & 0xf3) | 0x08;
                            cntrl2 = rx_adc_ctrl2 & 0x3f;
                        }
                        else
                        {// diversity2 && !psform.PSEnabled
                            P1_DDCConfig = 2;
                            DDCEnable = DDC0;
                            SyncEnable = DDC1;
                            Rate[0] = rx1_rate;
                            Rate[1] = rx1_rate;
                            cntrl1 = rx_adc_ctrl1 & 0xff;
                            cntrl2 = rx_adc_ctrl2 & 0x3f;
                        }
                    }

                    if (rx2_enabled)
                    {
                        DDCEnable += DDC3;
                        Rate[3] = rx2_rate;
                    }
                    break;
                case HPSDRModel.HERMES:
                case HPSDRModel.ANAN10:
                case HPSDRModel.ANAN100:
                    P1_rxcount = 4;                     // RX4 used for puresignal feedback
                    nddc = 4;
                    if (!mox)
                    {
                        if (!diversity2)
                        {
                            P1_DDCConfig = 4;
                            DDCEnable = DDC0;
                            SyncEnable = 0;
                            Rate[0] = rx1_rate;
                            cntrl1 = 0;
                            cntrl2 = 0;

                            if (rx2_enabled)
                            {
                                DDCEnable += DDC1;
                                Rate[1] = rx2_rate;
                            }
                        }
                        else
                        {
                            P1_DDCConfig = 5;
                            DDCEnable = DDC0;
                            SyncEnable = DDC1;
                            Rate[0] = rx1_rate;
                            Rate[1] = rx1_rate;
                            cntrl1 = 0;
                            cntrl2 = 0;
                        }
                    }
                    else
                    {
                        if (!diversity2 && !psform.PSEnabled)
                        {
                            P1_DDCConfig = 4;
                            DDCEnable = DDC0;
                            SyncEnable = 0;
                            Rate[0] = rx1_rate;
                            cntrl1 = 0;
                            cntrl2 = 0;

                            if (rx2_enabled)
                            {
                                DDCEnable += DDC1;
                                Rate[1] = rx2_rate;
                            }
                        }
                        else if (diversity2 && !psform.PSEnabled)
                        {
                            P1_DDCConfig = 5;
                            DDCEnable = DDC0;
                            SyncEnable = DDC1;
                            Rate[0] = rx1_rate;
                            Rate[1] = rx1_rate;
                            cntrl1 = 0;
                            cntrl2 = 0;
                        }
                        else // transmitting and PS is ON
                        {
                            P1_DDCConfig = 6;
                            DDCEnable = DDC0;
                            SyncEnable = DDC1;
                            Rate[0] = ps_rate;
                            Rate[1] = ps_rate;
                            cntrl1 = 4;
                            cntrl2 = 0;
                        }
                    }
                    break;

                case HPSDRModel.ANAN10E:
                case HPSDRModel.ANAN100B:
                    P1_rxcount = 2;                     // RX2 used for puresignal feedback
                    nddc = 2;
                    if (!mox)
                    {
                        if (!diversity2)
                        {
                            P1_DDCConfig = 4;
                            DDCEnable = DDC0;
                            SyncEnable = 0;
                            Rate[0] = rx1_rate;
                            cntrl1 = 0;
                            cntrl2 = 0;

                            if (rx2_enabled)
                            {
                                DDCEnable += DDC1;
                                Rate[1] = rx2_rate;
                            }
                        }
                        else
                        {
                            P1_DDCConfig = 5;
                            DDCEnable = DDC0;
                            SyncEnable = DDC1;
                            Rate[0] = rx1_rate;
                            Rate[1] = rx1_rate;
                            cntrl1 = 0;
                            cntrl2 = 0;
                        }
                    }
                    else
                    {
                        if (!diversity2 && !psform.PSEnabled)
                        {
                            P1_DDCConfig = 4;
                            DDCEnable = DDC0;
                            SyncEnable = 0;
                            Rate[0] = rx1_rate;
                            cntrl1 = 0;
                            cntrl2 = 0;

                            if (rx2_enabled)
                            {
                                DDCEnable += DDC1;
                                Rate[1] = rx2_rate;
                            }
                        }
                        else if (diversity2 && !psform.PSEnabled)
                        {
                            P1_DDCConfig = 5;
                            DDCEnable = DDC0;
                            SyncEnable = DDC1;
                            Rate[0] = rx1_rate;
                            Rate[1] = rx1_rate;
                            cntrl1 = 0;
                            cntrl2 = 0;
                        }
                        else // transmitting and PS is ON
                        {
                            P1_DDCConfig = 5;
                            DDCEnable = DDC0;
                            SyncEnable = DDC1;
                            Rate[0] = ps_rate;
                            Rate[1] = ps_rate;
                            cntrl1 = 4;
                            cntrl2 = 0;
                        }
                    }
                    break;

                case HPSDRModel.HPSDR:
                    break;
            }

            NetworkIO.EnableRxs(DDCEnable);
            NetworkIO.EnableRxSync(0, SyncEnable);
            for (i = 0; i < 4; i++)
                NetworkIO.SetDDCRate(i, Rate[i]);
            NetworkIO.SetADC_cntrl1(cntrl1);
            NetworkIO.SetADC_cntrl2(cntrl2);
            NetworkIO.CmdRx();
            NetworkIO.Protocol1DDCConfig(P1_DDCConfig, P1_diversity, P1_rxcount, nddc);

            //MW0LGE_21e
            if (!IsSetupFormNull) SetupForm.UpdateDDCTab();
        }

        public void GetDDC(out int DDCrx1, out int DDCrx2, out int DDCsync1, out int DDCsync2, out int DDCpsrx, out int DDCpstx)
        {
            //https://docs.google.com/spreadsheets/d/1DbOAXuhHRE1hMBwz8PyxL5FblW5BoiAUzksPaGM0NXY

            int rx1 = -1, rx2 = -1, sync1 = -1, sync2 = -1, psrx = -1, pstx = -1;

            int nME = MOX ? 1 : 0; // [0]
            int nDE = diversityForm != null && Diversity2 ? 1 : 0; // [1]
            int nPSE = psform.PSEnabled ? 1 : 0; // [2]

            int tot = nME + (nDE << 1) + (nPSE << 2);

            if (NetworkIO.CurrentRadioProtocol == RadioProtocol.ETH) // P2
            {
                switch (CurrentHPSDRHardware)
                {
                    case HPSDRHW.Angelia: // ANAN-100D
                    case HPSDRHW.Orion: // ANAN-200D
                    case HPSDRHW.OrionMKII: // AMAM-7000DLE 7000DLEMkII ANAN-8000DLE OrionMkII
                        switch (tot)
                        {
                            case 0: // off[2] off[1] off[0]
                                rx1 = 2;
                                rx2 = 3;
                                break;
                            case 1: // off off on
                                rx1 = 2;
                                rx2 = 3;
                                break;
                            case 2: // off on off
                                sync1 = 0;
                                sync2 = 1;
                                rx2 = 3;
                                break;
                            case 3: // off on on
                                sync1 = 0;
                                sync2 = 1;
                                rx2 = 3;
                                break;
                            case 4: // on off off
                                rx1 = 2;
                                rx2 = 3;
                                break;
                            case 5: // on off on
                                //sync1 = 0; //MW0LGE_22b no sync in map
                                //sync2 = 1;
                                rx1 = 2;
                                rx2 = 3;
                                break;
                            case 6: // on on off
                                sync1 = 0;
                                sync2 = 1;
                                rx2 = 3;
                                break;
                            case 7: // on on on
                                //sync1 = 0; //MW0LGE_22b no sync in map
                                //sync2 = 1;
                                rx1 = 2;
                                rx2 = 3;
                                break;
                        }
                        break;
                    //case HPSDRHW.Atlas: /// ???
                    case HPSDRHW.Hermes: // ANAN-10 ANAN-100 Heremes
                    case HPSDRHW.HermesII: // ANAN-10E ANAN-100B HeremesII
                        switch (tot)
                        {
                            case 0: // off off off
                                rx1 = 0;
                                rx2 = 1;
                                break;
                            case 1: // off off on
                                //sync1 = 0;  //MW0LGE_22b no sync in map
                                //sync2 = 1;
                                rx1 = 0;   //MW0LGE_22b missed out
                                rx2 = 1;
                                break;
                            case 2: // off on off
                                sync1 = 0;
                                sync2 = 1;
                                break;
                            case 3: // off on on
                                sync1 = 0;
                                sync2 = 1;
                                break;
                            case 4: // on off off
                                rx1 = 0;
                                rx2 = 1;
                                break;
                            case 5: // on off on
                                //sync1 = 0;  //MW0LGE_22b no sync in map
                                //sync2 = 1;
                                break;
                            case 6: // on on off
                                sync1 = 0;
                                sync2 = 1;
                                break;
                            case 7: // on on on
                                //sync1 = 0;  //MW0LGE_22b no sync in map
                                //sync2 = 1;
                                break;
                        }
                        break;
                }
            }
            else if (NetworkIO.CurrentRadioProtocol == RadioProtocol.USB) // P1
            {
                switch (CurrentHPSDRHardware)
                {
                    case HPSDRHW.Angelia: // ANAN-100D (all 5 adc)
                    case HPSDRHW.Orion: // ANAN-200D
                    case HPSDRHW.OrionMKII: // AMAM-7000DLE 7000DLEMkII ANAN-8000DLE OrionMkII
                        switch (tot)
                        {
                            case 0: // off off off
                                rx1 = 0;
                                rx2 = 2;
                                break;
                            case 1: // off off on
                                rx1 = 0;
                                rx2 = 2;
                                break;
                            case 2: // off on off
                                //rx1 = 0;
                                sync1 = 0;
                                sync2 = 1;
                                rx2 = 2;
                                break;
                            case 3: // off on on
                                //rx1 = 0;
                                sync1 = 0;
                                sync2 = 1;
                                rx2 = 2;
                                break;
                            case 4: // on off off
                                rx1 = 0;
                                rx2 = 2;
                                break;
                            case 5: // on off on
                                rx1 = 0;
                                rx2 = 2;
                                psrx = 3;
                                pstx = 4;
                                break;
                            case 6: // on on off
                                //rx1 = 0;
                                sync1 = 0;
                                sync2 = 1;
                                rx2 = 2;
                                break;
                            case 7: // on on on
                                //rx1 = 0;
                                sync1 = 0;
                                sync2 = 1;
                                rx2 = 2;
                                psrx = 3;
                                pstx = 4;
                                break;
                        }
                        break;
                    //                    case HPSDRHW.Atlas: /// ???
                    case HPSDRHW.Hermes: // ANAN-10 ANAN-100 Heremes (4 adc)
                        switch (tot)
                        {
                            case 0: // off off off
                                rx1 = 0;
                                rx2 = 1;
                                break;
                            case 1: // off off on
                                rx1 = 0;
                                rx2 = 1;
                                break;
                            case 2: // off on off
                                rx1 = 0;
                                rx2 = 1;
                                break;
                            case 3: // off on on
                                rx1 = 0;
                                rx2 = 1;
                                break;
                            case 4: // on off off
                                rx1 = 0;
                                rx2 = 1;
                                break;
                            case 5: // on off on
                                rx1 = 0;
                                rx2 = 1;
                                psrx = 2;
                                pstx = 3;
                                break;
                            case 6: // on on off
                                rx1 = 0;
                                rx2 = 1;
                                break;
                            case 7: // on on on
                                rx1 = 0;
                                rx2 = 1;
                                psrx = 2;
                                pstx = 3;
                                break;
                        }
                        break;
                    case HPSDRHW.HermesII: // ANAN-10E ANAN-100B HeremesII (2 adc)
                        switch (tot)
                        {
                            case 0: // off off off
                                rx1 = 0;
                                rx2 = 1;
                                break;
                            case 1: // off off on
                                rx1 = 0;
                                rx2 = 1;
                                break;
                            case 2: // off on off
                                rx1 = 0;
                                rx2 = 1;
                                break;
                            case 3: // off on on
                                rx1 = 0;
                                rx2 = 1;
                                break;
                            case 4: // on off off
                                rx1 = 0;
                                rx2 = 1;
                                break;
                            case 5: // on off on
                                psrx = 0;
                                pstx = 1;
                                break;
                            case 6: // on on off
                                rx1 = 0;
                                rx2 = 1;
                                break;
                            case 7: // on on on
                                psrx = 0;
                                pstx = 1;
                                break;
                        }
                        break;
                }
            }

            if (diversityForm != null && Diversity2)
            {
                // check if we are using RX1+RX2, RX1 or RX2
                switch (diversityForm.EXTDIVOutput)
                {
                    case 0: //rx1
                    case 2: //rx1+rx2
                        if(sync1 != -1) rx1 = sync1;
                        break;
                    case 1: //rx2
                        if (sync2 != -1) rx1 = sync2;
                        break;
                }
            }

            if (!MOX && rx1 == -1 && psrx != -1) rx1 = psrx;
            if (MOX && rx1 == -1 && pstx != -1) rx1 = pstx;

            if (rx2 == -1) rx2 = rx1;

            DDCrx1 = rx1;
            DDCrx2 = rx2;
            DDCsync1 = sync1;
            DDCsync2 = sync2;
            DDCpsrx = psrx;
            DDCpstx = pstx;
        }

