                            "VFOBTX : " + VFOBTX.ToString() + Environment.NewLine +
                            "VFOA : " + VFOAFreq.ToString() + Environment.NewLine +
                            "VFOB : " + VFOBFreq.ToString() + Environment.NewLine +
                            "VFOAsub : " + VFOASubFreq.ToString() + Environment.NewLine +
