            //MW0LGE_21d6
            //only here will we have a valid on or valid off
            if (m_bOldPower != chkPower.Checked)
            {
                PowerChangeHanders?.Invoke(m_bOldPower, chkPower.Checked);
                m_bOldPower = chkPower.Checked;
            }
