            }
            else
            {
                WDSP.SetChannelState(WDSP.id(0, 1), 0, 0);
                cmaster.SetAAudioMixWhat((void*)0, 0, 1, false);
                //radio.GetDSPRX(0, 0).RXOutputGain = 1.0;
                //radio.GetDSPRX(0, 0).Pan = 0.5f;
